package java_practice;

public class shift_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=-10;
		int c=a>>>1;
		System.out.println("a>>>1="+c);
		
		 int d = 10;
		 System.out.println(d++);
		 System.out.println(d);
		 System.out.println(++d);
	}

}
